/* eslint-disable no-extend-native */
/* eslint-disable jsx-a11y/no-redundant-roles */
import { useState } from "react"
import { RadioGroup } from "@headlessui/react"

Array.prototype.random = function () {
	return this[Math.floor(Math.random() * this.length)]
}

const product = {
	name: "Basic Tee 6-Pack",
	price: "$192",
	images: [
		{
			src: "https://tailwindui.com/img/ecommerce-images/product-page-02-secondary-product-shot.jpg",
			alt: "Two each of gray, white, and black shirts laying flat.",
		},
		{
			src: "https://tailwindui.com/img/ecommerce-images/product-page-02-tertiary-product-shot-01.jpg",
			alt: "Model wearing plain black basic tee.",
		},
		{
			src: "https://tailwindui.com/img/ecommerce-images/product-page-02-tertiary-product-shot-02.jpg",
			alt: "Model wearing plain gray basic tee.",
		},
		{
			src: "https://tailwindui.com/img/ecommerce-images/product-page-02-featured-product-shot.jpg",
			alt: "Model wearing plain white basic tee.",
		},
	],
	colors: [
		{
			id: 1,
			name: "Black",
			class: "bg-gray-900",
			selectedClass: "ring-gray-900",
		},
		{
			id: 2,
			name: "Gray",
			class: "bg-gray-200",
			selectedClass: "ring-gray-400",
		},
		{ id: 3, name: "White", class: "bg-white", selectedClass: "ring-gray-400" },
	],
	description:
		'The Basic Tee 6-Pack allows you to fully express your vibrant personality with three grayscale options. Feeling adventurous? Put on a heather gray tee. Want to be a trendsetter? Try our exclusive colorway: "Black". Need to add an extra pop of color to your outfit? Our white tee has you covered.',
}

function classNames(...classes) {
	return classes.filter(Boolean).join(" ")
}

export default function Product() {
	const [selectedColor, setSelectedColor] = useState(product.colors.random())

	const handleClick = () => {
		document.querySelector(".popup, .popup-content").classList.remove("active")
	}

	return (
		<div className="bg-white">
			<div className="pt-6">
				{/* Image gallery */}
				<div className="mt-6 max-w-2xl mx-auto sm:px-6 lg:max-w-7xl lg:px-8 lg:grid lg:grid-cols-3 lg:gap-x-8">
					<div
						className={`aspect-w-3 aspect-h-4 rounded-lg overflow-hidden lg:block ${
							selectedColor === undefined ? "" : "hidden"
						}`}
					>
						<img
							src={product.images[0].src}
							alt={product.images[0].alt}
							className="w-full h-full object-center object-cover"
						/>
					</div>
					<div
						className={`aspect-w-4 aspect-h-5 sm:rounded-lg sm:overflow-hidden lg:aspect-w-3 lg:aspect-h-4 ${
							selectedColor?.id === 1 ? "" : "hidden"
						}`}
					>
						<img
							src={product.images[1].src}
							alt={product.images[1].alt}
							className="w-full h-full object-center object-cover"
						/>
					</div>
					<div
						className={`aspect-w-4 aspect-h-5 sm:rounded-lg sm:overflow-hidden lg:aspect-w-3 lg:aspect-h-4 ${
							selectedColor?.id === 2 ? "" : "hidden"
						}`}
					>
						<img
							src={product.images[2].src}
							alt={product.images[2].alt}
							className="w-full h-full object-center object-cover"
						/>
					</div>
					<div
						className={`aspect-w-4 aspect-h-5 sm:rounded-lg sm:overflow-hidden lg:aspect-w-3 lg:aspect-h-4 ${
							selectedColor?.id === 3 ? "" : "hidden"
						}`}
					>
						<img
							src={product.images[3].src}
							alt={product.images[3].alt}
							className="w-full h-full object-center object-cover"
						/>
					</div>
				</div>

				{/* Product info */}
				<div className="max-w-2xl mx-auto pt-10 pb-16 px-4 sm:px-6 lg:max-w-7xl lg:pt-16 lg:pb-24 lg:px-8 lg:grid lg:grid-cols-3 lg:grid-rows-[auto,auto,1fr] lg:gap-x-8">
					<div className="lg:col-span-2 lg:border-r lg:border-gray-200 lg:pr-8">
						<h1 className="text-2xl font-extrabold tracking-tight text-gray-900 sm:text-3xl">
							{product.name}
						</h1>
						<div className="py-10 lg:pt-6 lg:pb-16 lg:col-start-1 lg:col-span-2 lg:border-r lg:border-gray-200 lg:pr-8">
							{/* Description and details */}
							<div>
								<h3 className="sr-only">Description</h3>

								<div className="space-y-6">
									<p className="text-base text-gray-900">
										{product.description}
									</p>
								</div>
							</div>
						</div>
					</div>

					{/* Options */}
					<div className="mt-4 lg:mt-0 lg:row-span-3">
						<h2 className="sr-only">Product information</h2>
						<p className="text-3xl text-gray-900">{product.price}</p>

						<form onSubmit={(e) => e.preventDefault()} className="mt-10">
							{/* Colors */}
							<div>
								<h3 className="text-sm text-gray-900 font-medium">Color</h3>

								<RadioGroup
									value={selectedColor}
									onChange={setSelectedColor}
									className="mt-4"
								>
									<RadioGroup.Label className="sr-only">
										Choose a color
									</RadioGroup.Label>
									<div className="flex items-center space-x-3">
										{product.colors.map((color) => (
											<RadioGroup.Option
												key={color.name}
												value={color}
												className={({ active, checked }) =>
													classNames(
														color.selectedClass,
														active && checked ? "ring ring-offset-1" : "",
														!active && checked ? "ring-2" : "",
														"-m-0.5 relative p-0.5 rounded-full flex items-center justify-center cursor-pointer focus:outline-none"
													)
												}
											>
												<RadioGroup.Label as="span" className="sr-only">
													{color.name}
												</RadioGroup.Label>
												<span
													aria-hidden="true"
													className={classNames(
														color.class,
														"h-8 w-8 border border-black border-opacity-10 rounded-full"
													)}
												/>
											</RadioGroup.Option>
										))}
									</div>
								</RadioGroup>
							</div>
							<button
								// onClick={handleClick}
								id="selectBtn"
								className="mt-10 w-full bg-indigo-600 border border-transparent rounded-md py-3 px-8 flex items-center justify-center text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
							>
								Select me
							</button>
						</form>
					</div>
				</div>
			</div>
		</div>
	)
}
